document.getElementById('agendamentoForm').addEventListener('submit',function(e){e.preventDefault();document.getElementById('mensagemSucesso').classList.remove('hidden');setTimeout(()=>{document.getElementById('mensagemSucesso').classList.add('hidden');document.getElementById('agendamentoForm').reset();},3000);});

fetch('http://localhost:3000/api/servicos')
  .then(res => res.json())
  .then(servicos => {
    const lista = document.getElementById('lista-servicos');
    servicos.forEach(s => {
      const item = document.createElement('li');
      item.textContent = `${s.nome} - ${s.preco}`;
      lista.appendChild(item);
    });
  });
